mapper.select:
SELECT DISTINCT ct.jar_name AS jar_name, mi.method_name AS method_name, mi.method_desc AS method_desc, mi.impl_class_name AS class_name FROM method_impl_table mi INNER JOIN class_table ct ON ct.class_name = mi.impl_class_name
AND mi.class_name = #{className}
AND mi.method_name = #{methodName}
AND mi.method_desc = #{methodDesc}

SELECT DISTINCT ct.jar_name AS jar_name, mi.method_name AS method_name, mi.method_desc AS method_desc, mi.class_name AS class_name FROM method_impl_table mi INNER JOIN class_table ct ON ct.class_name = mi.impl_class_name
AND mi.impl_class_name = #{className}
AND mi.method_name = #{methodName}
AND mi.method_desc = #{methodDesc}

SELECT DISTINCT
m.class_name FROM method_impl_table m
WHERE m.impl_class_name = #{className}

SELECT DISTINCT
m.impl_class_name FROM method_impl_table m
WHERE m.class_name = #{className}
